import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../client';
import './DetailView.css'; 

const DetailView = () => {
    const { id } = useParams();
    const [detail, setDetail] = useState(null);
    const [likeCount, setLikeCount] = useState(0); // State for like count
    const [comments, setComments] = useState([]); // State for comments
    const [newComment, setNewComment] = useState(''); // State for new comment input

    // Function to fetch comments
    const fetchComments = async () => {
        const { data, error } = await supabase
            .from('Comments')
            .select('*')
            .eq('postid', id);

        if (error) {
            console.error('Error fetching comments:', error);
        } else {
            setComments(data);
        }
    };

    useEffect(() => {
        const fetchDetail = async () => {
            const { data, error } = await supabase
                .from('Manure Posts')
                .select('*')
                .eq('id', id)
                .single();

            if (error) {
                console.error('Error fetching details:', error);
            } else {
                setDetail(data);
                setLikeCount(data.likes); // Set initial like count from fetched data
            }
        };

        // Fetch comments when component mounts
        fetchComments();
        fetchDetail();
    }, [id]);

    // Function to handle like button click
    const handleLikeClick = async () => {
        const newLikeCount = likeCount + 1; // Increment like count
        setLikeCount(newLikeCount); // Update like count in state

        // Update like count in the database (optional)
        await supabase
            .from('Manure Posts')
            .update({ likes: newLikeCount })
            .eq('id', id);
    };

    // Function to handle new comment submission
    const handleSubmitComment = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;

        // Upload new comment to the database
        await supabase
            .from('Comments')
            .insert([{ postid: id, content: newComment }]);

        // Reset new comment input
        setNewComment('');
        
        // Refetch comments to update the UI
        fetchComments();
    };

    // Function to handle edit button click
    const handleEditClick = () => {
        // Redirect to edit post page
        // You need to replace '/editpost/' with the actual URL of the edit post page
        window.location.href = `/edit/${id}`;
    };

    // Function to handle delete button click
    const handleDeleteClick = async () => {
        // Delete post from the database
        await supabase
            .from('Manure Posts')
            .delete()
            .eq('id', id);
        
        // Redirect to home page or any other page after deletion
        // You need to replace '/' with the actual URL of the desired page
        window.location.href = '/';
    };

    if (!detail) return <div className="DetailView Loading">Loading...</div>;

    return (
        <div className="DetailView">
            <button className="editButton" onClick={handleEditClick}>Edit</button>
            <button className="deleteButton" onClick={handleDeleteClick}>Delete</button>
            <h1>{detail.name}</h1>
            <h2>{detail.type}</h2>
            <p>{detail.description}</p>
            <button className="likeButton" onClick={handleLikeClick}>
                ðŸš½ {likeCount}
            </button>
            <div>
                <h3>Comments</h3>
                <ul>
                    {comments.map(comment => (
                        <li key={comment.id}>{comment.content}</li>
                    ))}
                </ul>
                <form onSubmit={handleSubmitComment}>
                    <input
                        type="text"
                        value={newComment}
                        onChange={e => setNewComment(e.target.value)}
                        placeholder="Add a comment..."
                    />
                    <button type="submit">Post Comment</button>
                </form>
            </div>
        </div>
    );
}

export default DetailView;
