import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // useHistory hook for navigation
import { supabase } from '../client';
import './Card.css';
import more from './more.png';

const Card = ({ id, title, created, likes }) => {
    const [count, setCount] = useState(likes);
    const history = useHistory(); // Hook for redirecting to details page

    const updateCount = async (e) => {
        e.stopPropagation(); // Prevent the click from bubbling up to the card div
        const newCount = count + 1;
        setCount(newCount);

        const { error } = await supabase
            .from('Characters')
            .update({ likes: newCount })
            .eq('id', id);

        if (error) {
            console.error('Error updating likes:', error);
        }
    };

    const navigateToDetails = () => {
        history.push(`/details/${id}`);
    };

    return (
        <div className="Card" onClick={navigateToDetails}>
            <div className="Card-Header">
                <img className="moreButton" alt="edit button" src={more} onClick={(e) => {
                    e.stopPropagation(); // Prevent click from navigating to details
                    history.push(`/edit/${id}`);
                }} />
            </div>
            <div className="Card-Content">
                <h2 className="title">{title}</h2>
                <p className="created">{created}</p>
            </div>
            <div className="Card-Footer">
                <div className="like" onClick={updateCount}>ðŸš½ {count}</div>
            </div>
        </div>
    );
};

export default Card;
