import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { supabase } from '../client';
import './Card.css';
import more from './more.png';

const Card = ({ id, title, created, likes }) => {
    const [count, setCount] = useState(likes);
    const history = useHistory();

    const updateCount = async (e) => {
        e.stopPropagation(); // Prevent click from bubbling to the card's onClick event
        const newCount = count + 1;
        setCount(newCount);

        try {
            const { error } = await supabase
                .from('Characters')
                .update({ likes: newCount })
                .eq('id', id);

            if (error) {
                console.error('Error updating likes:', error);
                setCount(count); // Revert the like count if there's an error
            }
        } catch (error) {
            console.error('Error updating likes:', error.message);
            setCount(count); // Revert the like count if there's an error
        }
    };

    const navigateToDetails = () => {
        history.push(`/details/${id}`);
    };

    const navigateToEdit = (e) => {
        e.stopPropagation();
        history.push(`/edit/${id}`);
    };

    return (
        <div className="Card" onClick={navigateToDetails}>
            <div className="Card-Header" onClick={navigateToEdit}>
                <img className="moreButton" alt="edit button" src={more} />
            </div>
            <div className="Card-Content">
                <h2 className="title">{title}</h2>
                <p className="created">{created}</p>
            </div>
            <div className="Card-Footer">
                <div className="like" onClick={updateCount}>ðŸš½ {count}</div>
            </div>
        </div>
    );
};

export default Card;
