import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom';
import ReadPosts from './pages/ReadPosts';
import CreatePost from './pages/CreatePost';
import EditPost from './pages/EditPost';
import DetailView from './pages/DetailView'; // Import the DetailView component
import { Link } from 'react-router-dom';

const App = () => {

  const descr = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.'

  const posts = [
      {'id':'1', 'title': 'Cartwheel in Chelsea ü§∏üèΩ‚Äç‚ôÄÔ∏è', 'author':'Harvey Milian', 'description': descr},
      {'id':'2', 'title': 'Love Lock in Paris üîí', 'author':'Beauford Delaney', 'description': descr},
      {'id':'3', 'title': 'Wear Pink on Fridays üéÄ', 'author':'Onika Tonya', 'description': descr},
      {'id':'4', 'title': 'Adopt a Dog üê∂', 'author':'Denise Michelle', 'description': descr},
  ]

  // Sets up routes
  let element = useRoutes([
    { path: "/", element: <ReadPosts data={posts}/> },
    { path: "/edit/:id", element: <EditPost data={posts} /> },
    { path: "/new", element: <CreatePost /> },
    { path: "/details/:id", element: <DetailView /> }, // Adding route for detail view
  ]);

  return (
    <div className="App">
      <div className="header">
        <h1>üëç Character Creator 1.0</h1>
        <Link to="/"><button className="headerBtn"> Explore Character üîç </button></Link>
        <Link to="/new"><button className="headerBtn"> Create Character üèÜ </button></Link>
      </div>
      {element}
    </div>
  );
}

export default App;
