import React, { useState } from 'react'; // Corrected import
import './CreatePost.css';
import { supabase } from '../client';

function CreatePost() {
    // Initialize post state
    const [post, setPost] = useState({title: "", author: "", description: ""});

    // Update post state on input change
    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost(prev => ({
            ...prev,
            [name]: value,
        }));
    };

    // Handle form submit
    const handleSubmit = async (event) => {
        event.preventDefault(); // Prevent default form submission
        // Insert data into Supabase
        const { error } = await supabase
            .from('Posts')
            .insert([{
                title: post.title, 
                author: post.author, 
                description: post.description
            }]);
        if (error) {
            console.error('Error inserting data:', error);
        } else {
            window.location = "/"; // Redirect after successful insertion
        }
    };

    // Render form
    return (
        <div>
            <form onSubmit={handleSubmit}> {/* Use onSubmit here */}
                <label htmlFor="name">name</label> <br />
                <input type="text" id="title" name="title" value={post.title} onChange={handleChange} /><br /><br />

                <label htmlFor="class">class</label><br />
                <input type="text" id="author" name="author" value={post.author} onChange={handleChange} /><br /><br />

                <label htmlFor="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" name="description" value={post.description} onChange={handleChange}></textarea><br />

                <input type="submit" value="Submit" />                
            </form>
        </div>
    );
}

export default CreatePost;
