    import React, { useEffect, useState } from 'react';
    import { useParams } from 'react-router-dom';
    import { supabase } from '../client';
    import './DetailView.css'; 

    const DetailView = () => {
        const { id } = useParams();
        const [detail, setDetail] = useState(null);
        const [likeCount, setLikeCount] = useState(0); // State for like count

        useEffect(() => {
            const fetchDetail = async () => {
                const { data, error } = await supabase
                    .from('Manure Posts')
                    .select('*')
                    .eq('id', id)
                    .single();

                if (error) {
                    console.error('Error fetching details:', error);
                } else {
                    setDetail(data);
                    setLikeCount(data.likes); // Set initial like count from fetched data
                }
            };

            fetchDetail();
        }, [id]);

        // Function to handle like button click
        const handleLikeClick = async () => {
            const newLikeCount = likeCount + 1; // Increment like count
            setLikeCount(newLikeCount); // Update like count in state

            // Update like count in the database (optional)
            await supabase
                .from('Manure Posts')
                .update({ likes: newLikeCount })
                .eq('id', id);
        };

        if (!detail) return <div className="DetailView Loading">Loading...</div>;

        return (
            <div className="DetailView">
                <h1>{detail.name}</h1>
                <h2>{detail.type}</h2>
                <p>{detail.description}</p>
                <button className="likeButton" onClick={handleLikeClick}>
                    ðŸš½ {likeCount}
                </button>
            </div>
        );
    }

    export default DetailView;
