import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Use useHistory for navigation
import { supabase } from '../client';
import './Card.css';
import more from './more.png';

const Card = ({ id, title, created, likes }) => {
    const [count, setCount] = useState(likes); // Initialize the likes count
    const history = useHistory(); // Use this to navigate programmatically

    const updateCount = async (e) => {
        e.stopPropagation(); // Prevents the card click event from firing when the like is clicked
        const newCount = count + 1;
        setCount(newCount);

        // Update the likes count in the database
        const { error } = await supabase
            .from('Characters') // Assuming 'Characters' is your table name
            .update({ likes: newCount })
            .eq('id', id);

        if (error) {
            console.error('Error updating likes:', error);
        }
    };

    // Function to navigate to the details page when the card is clicked
    const navigateToDetails = (e) => {
        e.stopPropagation(); // In case this event is bubbled from children
        history.push(`/details/${id}`);
    };

    // Function to navigate to the edit page when the edit icon is clicked
    const navigateToEdit = (e) => {
        e.stopPropagation(); // Prevent card click event from firing
        history.push(`/edit/${id}`);
    };

    return (
        <div className="Card" onClick={navigateToDetails}>
            <img 
                className="moreButton" 
                alt="edit button" 
                src={more} 
                onClick={navigateToEdit} 
            />
            <h2 className="title">{title}</h2>
            <p className="created">{created}</p>
            {/* The like button is now a div that looks and behaves like a button */}
            <div className="like" onClick={updateCount}>ðŸš½ {count}</div>
        </div>
    );
};

export default Card;
